select gas_id gasId,user_id userId, gas_account gasAccount,company, owner owner ,phone,status,sign
from gas_card


	and user_id = #{gasCard.userId}


	and gas_id = #{gasCard.gasId}


	and gas_account = #{gasCard.gasAccount}


	and owner = #{gasCard.owner}


	and company = #{gasCard.company}


	and phone = #{gasCard.phone}


	and status = #{gasCard.status}


order by gas_id 

	limit #{pageBean.start}, #{pageBean.rows}

