mapper.select:
select id ,coupon_id couponId, coupon_package_id couponPackageId
from package_coupons


	and coupon_id = #{packageCoupons.couponId}


	and coupon_package_id = #{packageCoupons.couponPackageId}


order by id 

	limit #{pageBean.start}, #{pageBean.rows}

mapper.delete:
 	delete from package_coupons 


		and id = #{packageCoupons.id}


		and coupon_package_id = #{packageCoupons.couponPackageId}
