mapper.select:
select coupon_id couponId,coupon_name couponName, type, is_deliver isDeliver,sum , coupon_desc couponDesc,create_time createTime
from coupon


	and coupon_name  like concat('%',#{coupon.couponName},'%') 


	and type = #{coupon.type}


	and is_deliver = #{coupon.isDeliver}


order by coupon_id desc

	limit #{pageBean.start}, #{pageBean.rows}
